  @Test
  public void commandGetGpsInfo() {

    long gpsDate = 123;
    long gpsTime = 456;
    long lon = 789;
    long lat = 123;

    CommandGetGpsinfo command = new CommandGetGpsinfo(mockLiveDataParser);
    command.sendCommand();
    verify(mockLiveDataParser).executeCommand(Commands.G.toString());

    String testString = "GPS Time = " + gpsTime + System.lineSeparator() +
        "GPS Date = " + gpsDate + System.lineSeparator() +
        "Lat = " + lat + System.lineSeparator() +
        "Lon = " + lon + System.lineSeparator();

    for (char c : testString.toCharArray()) {
      command.receive(c);
    }

    assertEquals(gpsDate, deviceStatus.getGpsDate());
    assertEquals(gpsTime, deviceStatus.getGpsTime());
    assertEquals(lat, deviceStatus.getLat(), 0);
    assertEquals(lon, deviceStatus.getLongitude(), 0);

  }

  @Test
  public void commandInizializeToAutommode() {
    CommandInitialize command = new CommandInitialize(mockLiveDataParser);

    command.sendCommand();
    verify(mockLiveDataParser).executeCommand(Commands.B.toString());

    String testString = "; ;" + System.lineSeparator() +
        "App?";

    for (char c : testString.toCharArray()) {
      command.receive(c);
    }
    verify(mockLiveDataParser).executeCommand("100");
    verify(mockLiveDataParser).addCommandToQueue(any(CommandGetGpsinfo.class));
    verify(mockLiveDataParser).addCommandToQueue(any(CommandGetCalibration.class));
    verify(mockLiveDataParser).addCommandToQueue(any(CommandAutoMode.class));
    verify(mockLiveDataParser).runNextCommand();

  }


  @Test
  public void commandInizializeToIdle() {
    CommandInitialize command = new CommandInitialize(mockLiveDataParser);

    command.sendCommand();
    verify(mockLiveDataParser).executeCommand(Commands.B.toString());

    String testString = "App?";

    for (char c : testString.toCharArray()) {
      command.receive(c);
    }
    verify(mockLiveDataParser).executeCommand("100");
    verify(mockLiveDataParser).addCommandToQueue(any(CommandGetGpsinfo.class));
    verify(mockLiveDataParser).addCommandToQueue(any(CommandGetCalibration.class));
    verify(mockLiveDataParser).addCommandToQueue(any(CommandIdle.class));
    verify(mockLiveDataParser).runNextCommand();

  }

  @Test
  public void commandAny() {
    String commandString = "testcommand";

    CommandAny command = new CommandAny(mockLiveDataParser, commandString);
    command.sendCommand();
    verify(mockLiveDataParser).executeCommand(commandString);
  }


