  @Test
  public void commandGetConfiguration() {

    long testvalue = 200;
    String on = "on";
    String off = "off";
    String[] fldPixels = {"4444", "4444", "4444", "4444", "4444"};

    CommandGetConfiguration command = new CommandGetConfiguration(mockLiveDataParser);
    command.sendCommand();
    verify(mockLiveDataParser).executeCommand(Commands.c.toString());

    String testString =
        "Max IT[ms] = " + testvalue + System.lineSeparator() +
            "Interval[s] =" + testvalue + System.lineSeparator() +
            "Serial resolution = " + testvalue + System.lineSeparator() +
            "Serial stream" + on + System.lineSeparator() +
            "Serial data transfer" + off + System.lineSeparator() +
            "LED on each:" + testvalue + System.lineSeparator() +
            "LED power:" + testvalue + System.lineSeparator() +
            "3FLD pixels" + System.lineSeparator() +
            fldPixels[0] + System.lineSeparator() +
            fldPixels[1] + System.lineSeparator() +
            fldPixels[2] + System.lineSeparator() +
            fldPixels[3] + System.lineSeparator() +
            fldPixels[4] + System.lineSeparator() +
            "QE averages = " + testvalue + System.lineSeparator() +
            "FLAME averages = " + testvalue + System.lineSeparator() +
            "config.txt written" + System.lineSeparator();

    for (char c : testString.toCharArray()) {
      command.receive(c);
    }

    assertEquals(testvalue, deviceStatus.getMaxIntegrationTime());
    assertEquals(testvalue, deviceStatus.getIntervalTime());
    assertEquals(testvalue, deviceStatus.getSerialResolution());
    assertTrue(deviceStatus.isSerialStream());
    assertFalse(deviceStatus.isSerialDataTransfer());
    assertEquals(testvalue, deviceStatus.getLedOnEachCycle());
    assertEquals(testvalue, deviceStatus.getLedPower());
    assertEquals(Arrays.toString(fldPixels), deviceStatus.getFldPixels());
    assertEquals(testvalue, deviceStatus.getQeAverages());
    assertEquals(testvalue, deviceStatus.getFlameAverages());

    verify(mockLiveDataParser).runNextCommand();

  }

