  @Test
  public void commandManualMode() {
    CommandManualMode command = new CommandManualMode(mockLiveDataParser);
    command.sendCommand();
    verify(mockLiveDataParser).executeCommand(Commands.C.toString());


    when(mockLiveDataParser.getCommandQueue()).thenReturn(mock(ArrayDeque.class));

    String testString = "awaiting commands..." + System.lineSeparator();

    for (char c : testString.toCharArray()) {
      command.receive(c);
    }

    verify(mockLiveDataParser).runNextCommand();
  }

  @Test
  public void commandSetInterval() {
    int interval = 30;

    CommandSetInterval command = new CommandSetInterval(mockLiveDataParser, interval);
    command.sendCommand();

    verify(mockLiveDataParser).executeCommand(Commands.i.toString() + " " + interval);


    String testString = "Interval[s] = " + interval + System.lineSeparator() +
        "config.txt written" + System.lineSeparator();

    for (char c : testString.toCharArray()) {
      command.receive(c);
    }

    assertEquals(interval, deviceStatus.getIntervalTime());

    verify(mockLiveDataParser).runNextCommand();
  }

  @Test
  public void commandSetIntegrationTime() {
    int integrationtime = 30;

    CommandSetItegrationTime command = new CommandSetItegrationTime(mockLiveDataParser, integrationtime);
    command.sendCommand();

    verify(mockLiveDataParser).executeCommand(Commands.I.toString() + " " + integrationtime);

    String testString = "IT = " + integrationtime + System.lineSeparator() +
        "config.txt written" + System.lineSeparator();

    for (char c : testString.toCharArray()) {
      command.receive(c);
    }

    assertEquals(integrationtime, deviceStatus.getIntegrationTimeConfigured());

    verify(mockLiveDataParser).runNextCommand();

  }

